.modal4-root {
  border-radius: {{radius}}px;
  box-shadow: 0 {{shadow}}px {{shadow*3}}px rgba(0, 0, 0, 0.5);
  opacity: 0;
  transform: scale(1.1);
  transition: {{_duration}}ms all ease-out;
}

.modal4-root.is-active {
  opacity: 1;
  transform: scale(1);
}

.modal4-root.is-hiding {
  opacity: 0;
  transform: scale(0.9);
}

.modal4-panel1 {
  padding: 15px;
  background-color: {{bgcolor1}};
  border-top-left-radius: {{radius}}px;
  border-top-right-radius: {{radius}}px;
}

.modal4-panel2 {
  padding: 15px;
  background-color: {{bgcolor2}};
  border-bottom-left-radius: {{radius}}px;
  border-bottom-right-radius: {{radius}}px;
}

.modal4-close {
  cursor: pointer;
  background-color: {{closecolor==='white'?'white':'black'}};
  border: 2px solid {{closecolor==='white'?'black':'white'}};
  border-radius: 100px;
  position: absolute;
  width: 30px;
  height: 30px;
  padding: 0;
  top: -10px;
  right: -10px;
  transition: transform 200ms;
}

.modal4-close-h, .modal4-close-v {
  background-color: {{closecolor==='white'?'black':'white'}};
  display: block;
  width: 16px;
  height: 2px;
  position: absolute;
  left: 50%;
  top: 50%;
}

.modal4-close-h {
  transform: translate(-50%, -50%) rotate(135deg);
}

.modal4-close-v {
  transform: translate(-50%, -50%) rotate(45deg);
}

.modal4-close:hover {
  transform: scale(1.1);
}

.form1-cover {
  padding: {{cover_vpaddings}}px {{cover_hpaddings}}px;
  background-color: {{cover_bgcolored?cover_bgcolor:"transparent"}};
  border-color: {{cover_bordercolor}};
  border-style: {{cover_border?"solid":"none"}};
  border-width: {{cover_borderwidth}}px;
  border-radius: {{cover_radius}}px;
  box-shadow: 0 {{cover_shadow/3}}px {{cover_shadow}}px 0px rgba(0, 0, 0, 0.1);
}

/* _fields_delimiter_ */[cr-field] {
  padding-bottom: {{margins}}px;
  font-size: {{text_size}}px;
  font-family: {{text_font}};
}

.name {
  font-weight: {{title_style==='bold'?'bold':'normal'}};
  font-family: {{title_font}};
  opacity: 0.8;
  margin-bottom: {{title_size/2}}px;
  font-size: {{title_size}}px;
  word-wrap: break-word;
  text-transform: {{title_style==='caps'?'uppercase;':'normal'}};
  letter-spacing: {{title_style==='caps'?'0.1em;':'none'}};
}

.form-control {
  height: auto;
  line-height: 1.2;
  box-shadow: none;
  position: relative;
  background-color: {{input_color}};
  color: {{input_color.l_inv(1).a_set(input_text_opacity/100)}};
  border: {{input_border_width}}px {{input_border?'solid':'none'}} {{input_border_color.a_set(input_border_alpha/100)}};
  border-radius: {{input_radius}}px;
  padding: {{input_size}}px {{input_size / 1.25}}px;
  font-size: {{text_size}}px;
  cursor: initial;
}

.form-control:focus,
.form-control:focus-within {
  border-color: {{color}};
  background-color: {{input_color.l_inv(0.01)}};
}

.form-control + .form-control {
  margin-top: 10px;
}

label {
  font-weight: normal;
  display: block;
  cursor: pointer;
}

textarea {
  resize: vertical;
}

.checkbox {
  width: {{checkbox_size}}px;
  height: {{checkbox_size}}px;
  padding: 0;
  margin: 0 {{checkbox_size/4}}px 0 0;
  line-height: 0;
  min-height: auto;
  display: inline-block;
  vertical-align: middle;
  background: white;
  border: 2px solid rgba(0, 0, 0, 0.25);
  position: relative;
  transition: 300ms all;
  border-radius: 2px;
}

.checkbox svg {
  width: 100%;
  opacity: 0;
  transform: scale(0);
  transition: 300ms all;
}

input:hover ~ .checkbox,
input:focus ~ .checkbox {
  border-color: {{color.l_inc(0.5)}};
}

input:checked ~ .checkbox {
  border-color: {{color.l_dec(0.1)}};
  background-color: {{color}};
}

input:active ~ .checkbox {
  box-shadow: inset 0 0 5px rgba(0, 0, 0, 0.15);
}

input:checked ~ .checkbox svg {
  opacity: 1;
  transform: scale(1);
}

.radio {
  margin: 0;
  margin: 0 {{checkbox_size/4}}px 0 0;
  padding: 0;
  line-height: 0;
  width: {{checkbox_size}}px;
  height: {{checkbox_size}}px;
  min-height: auto;
  display: inline-block;
  vertical-align: middle;
  background: white;
  border: 2px solid rgba(0, 0, 0, 0.25);
  position: relative;
  transition: 300ms all;
  border-radius: 100px;
}

.radio:before {
  position: absolute;
  left: 0;
  width: 100%;
  top: 0;
  height: 100%;
  content: '';
  display: block;
  background-color: white;
  border-radius: 100px;
  opacity: 0;
  transform: scale(0);
  transition: 300ms all;
}

input:hover ~ .radio, input:focus ~ .radio {
  border-color: {{color.l_inc(0.5)}};
}

input:active ~ .radio {
  box-shadow: inset 0 0 5px rgba(0, 0, 0, 0.15);
}

input:checked ~ .radio {
  border-color: {{color.l_dec(0.1)}};
  background-color: {{color}};
}

input:checked ~ .radio:before {
  opacity: 1;
  transform: scale(0.5);
}

